;;ROBIN.ASM    (c)1989 B.RIVE;	read	"LINK.ASM"	write direct "a:ROBIN.BIN",BUFOVL	ORG  BUFOVL;;ROBIN (HL-->ROB_HELP,A-=1 RAID, =2 CONQUETE);ROBIN         PUSH BC         PUSH HL         PUSH AF         LD   HL,NORMLONG	call load_decomp_scr	call SETPAL         LD   HL,ROBMUS         CALL LOADMUS         POP  AF         POP  HL         BIT  7,(HL)         JR   NZ,DEJAFAIT         PUSH BC         LD   C,A         LD   A,(HL)         AND  #03         LD   A,C         POP  BC         JR   Z,TOOMUCH         SET  7,(HL)         DEC  A         JR   Z,POURRAID         LD   HL,OKCONQ         JR   AFFITPOURRAID LD   HL,OKRAID         JR   AFFITDEJAFAIT LD   HL,DEJA         JR   AFFITTOOMUCH  LD   HL,TROPAFFIT    LD   A,10         CALL PLACARD         CALL OFF         POP  BC         RET;	read "TEXTE_ROBIN.ASM"